, asset_cd AS assetCd , sw_type AS swType , sw_nm AS swNm , sw_info AS swInfo , sw_dir AS swDir , sw_user AS swUser , sw_etc AS swEtc , audit_day AS auditDay , audit_file_cd AS auditFileCd , user_regi AS userRegi , sw_info_sort_num AS swInfoSortNum , create_date AS createDate , url_service_target AS urlServiceTarget , url_target_state AS urlTargetState , closed_due_date AS closedDueDate , except_reason AS exceptReason , pre_chg_date AS preChgDate , last_chg_date AS lastChgDate , chg_page_cnt AS chgPageCnt , added_chg_page_cnt AS addedChgPageCnt , added_chg_cnt AS addedChgCnt , represent_url AS representUrl , url_management_cd AS urlManagementCd , url_service_nm AS urlServiceNm , url_person_flag AS urlPersonFlag , url_location_flag AS urlLocationFlag , url_charge_flag AS urlChargeFlag , url_board_flag AS urlBoardFlag , url_server_flag AS urlServerFlag
FROM SNET_ASSET_SW_AUDIT_DAY WHERE asset_cd = #{assetCd, javaType=String, jdbcType=VARCHAR} AND sw_type = #{swType, javaType=String, jdbcType=VARCHAR} AND sw_nm = #{swNm, javaType=String, jdbcType=VARCHAR} AND sw_info = #{swInfo, javaType=String, jdbcType=VARCHAR} AND sw_dir = #{swDir, javaType=String, jdbcType=VARCHAR} AND sw_user = #{swUser, javaType=String, jdbcType=VARCHAR} AND sw_etc = #{swEtc, javaType=String, jdbcType=VARCHAR}
